function openSearchOverlay(){const e=document.querySelector(".search-menu"),t=document.querySelector(".modalDialog"),n=document.querySelector(".dialogClose"),o=document.querySelector(".searchField");e.addEventListener("click",()=>{t.style.opacity="1",t.style.pointerEvents="auto",n.style.pointerEvents="auto",o.style.pointerEvents="auto"}),n.addEventListener("click",()=>{t.style.opacity="0",t.style.pointerEvents="none",n.style.pointerEvents="none",o.style.pointerEvents="none"})}function openCloseMenu(){const e=document.getElementById("menu-icon"),t=document.getElementById("menu-main");e.addEventListener("click",n=>{n.target.classList.contains("clicked")?(e.classList.remove("clicked"),t.classList.remove("active")):(e.classList.add("clicked"),t.classList.add("active"))})}window.onload=(()=>{openSearchOverlay(),openCloseMenu()});